import { useState } from "react"

function App(){

  //Etat State

  const [providers, Setproviders] = useState([
    {id : 1, name : "fournisseur"},
    {id : 2, name : "Glowme" },
    {id : 3, name : "Red" }
  ]);

  //Comportement

  const handleDelete= (id) =>{
    console.log(id)

    //copie du state
    const providersCopy = [...providers]

    //Manipuler mon state
    const providersCopyUpdated = providersCopy.filter(provider => provider.id !== id)

    //actualiser le state
    Setproviders(providersCopyUpdated);


  }

  //Affichage (render)

  return (
  <div>
    <h1>Providers List</h1>
    <ul>
      {providers.map((provider) =>  
      <li key ={provider.id}>{provider.name} <button onClick ={()=> handleDelete(provider.id)}>X</button></li>) }
    </ul>
    <form action= "submit">
      <input type = "text" placeholder= "Add new provider"/>
    </form>
  </div>);
}

export default App;
